from openerp import models, fields, api, _

class ResPartner(models.Model):
    _name = 'res.partner'

    name = fields.Char('Name', required=True)
    phone = fields.Char('Phone')
    mobile = fields.Char('Mobile')
    email = fields.Char('Email')

    @api.multi
    def get_name(self):
        return self.name
